import React from 'react';
// import logo from "../img/logo/logo.png";


//   import { Link } from 'react-router';
window.onload = function () {
   const hamburger = document.querySelector(".hamburger");
   const navMenu = document.querySelector(".nav-menu");

   hamburger.addEventListener("click", mobileMenu);

   function mobileMenu() {
      hamburger.classList.toggle("active");
      navMenu.classList.toggle("active");
   }
   const navLink = document.querySelectorAll(".nav-link");

   navLink.forEach(n => n.addEventListener("click", closeMenu));

   function closeMenu() {
      hamburger.classList.remove("active");
      navMenu.classList.remove("active");
   }
}
   export default class Nav extends React.Component {
    render(props) { 
        let content = {
            English: {
              title: "Lorem",
              description:
                "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book."
            },
            Malayalam: {
              title: "ലോറെം ഇപ്സിയം",
              description:
                "പ്രിന്റ് ആൻഡ് ടൈപ്പുചെയ്യൽ വ്യവസായത്തിന്റെ ഡമ്മി കോഡാണ് ലോറെം ഇപ്സിയം . 1500-കൾ മുതൽ ലൊറെം ഇപ്സം വ്യവസായത്തിന്റെ സ്റ്റാൻഡേർഡ് ഡമ്മി പാഠമാണ്, അജ്ഞാതമായ ഒരു പ്രിന്റർ ഒരു തരം ഗാലക്സി എടുത്ത് ഒരു സ്പെസിഫിക്കേഷൻ ബുക്ക് ഉണ്ടാക്കാൻ അതിനെ അണിനിരത്തി"
            }
          };

          props.language === "Malayalam"
            ? (content = content.Malayalam)
            : (content = content.English)

      return (

            <header className="header" id="#nav">
                    <nav className="navbar navbar-fixed-top">
                    <div className="language-select">
          <select
            className="custom-select"
            value={props.language}
            onChange={e => props.handleSetLanguage(e.target.value)}
          >
            <option value="English">English</option>
            <option value="Malayalam">Malayalam</option>
          </select>
          </div>
                        <a href="#pageC" className="nav-logo"></a> 
                        {/* <img id="myImage" src={logo}/> */}
                        <ul className="nav-menu">
                            <li className="nav-item">
                                <a href="#carousel" className="nav-link">INICIO</a>
                            </li>
                            <li className="nav-item">
                            <a href="#carousel">
                        PERSONALISE{" "}
                        <p className="subtopnav">
                        Sua&nbsp;imagem!
                        </p>
                        </a>                </li>
                            <li className="nav-item">
                            <a href="#pageC">
                        ANIME
                        <p className="subtopnav">
                        Suas&nbsp;ideias!
                        </p>
                        </a>                </li>
                            <li className="nav-item">
                            <a href="#contact">CONTATO</a>
                            </li>
                        </ul>
                        <div className="hamburger">
                            <span className="bar"></span>
                            <span className="bar"></span>
                            <span className="bar"></span>
                        </div>
                    </nav>
                    <h1 class="display-4 text-white mt-5 mb-2">{content.title}</h1>
            <p class="lead mb-5 text-white-50">{content.description}</p>
            </header>


         );
    }
  }
